import React, { Component } from "react";
import { Col, Row } from "react-bootstrap";
import Formulir from "./Formulir";

import NavbarComponent from "./NavbarComponent";
import Tabel from "./Tabel";
import Voting from "./Voting";

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      kandidate: [],
      nama: "",
      vote: 0,
      id: "",
      radio: "",
    };
  }

  handleChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
    });
  };

  handleSubmit = (event) => {
    event.preventDefault();

    this.setState({
      kandidate: [
        ...this.state.kandidate,
        {
          id: this.state.kandidate.length + 1,
          nama: this.state.nama,
          vote: 0,
          radio: 0,
        },
      ],
    });
  };

  handleVote = (event) => {
    event.preventDefault();

    const bukanpilih = this.state.kandidate
      .filter((noncalon) => noncalon.id !== 1)
      .map((filternoncalon) => {
        return filternoncalon;
      });
    console.log(bukanpilih);
    const pilih = this.state.kandidate
      .filter((calon) => calon.id === 1)
      .map((filtercalon) => {
        this.setState({
          kandidate: [
            ...bukanpilih,
            {
              vote: filtercalon.vote + 1,
            },
          ],
        });
      });
    console.log(pilih[0].nama);
    console.log(this.state.radio);

    // this.setState({
    //   kandidate: [
    //     ...bukanpilih,
    //     {
    //       vote: this.state.vote + 1,
    //     },
    //   ],
    // });
  };

  handleRadio = (event) => {
    event.preventDefault();
    console.log(event.currentTarget.value);

    this.setState({
      radio: event.currentTarget.value,
    });
  };

  render() {
    return (
      <>
        <NavbarComponent />
        <div className="container mt-5">
          <Row>
            <Col lg="4">
              <Formulir
                {...this.state}
                handleChange={this.handleChange}
                handleSubmit={this.handleSubmit}
              />
              <br></br>
              <Tabel kandidate={this.state.kandidate} />
            </Col>
            <Col>
              <Voting
                kandidate={this.state.kandidate}
                handleVote={this.handleVote}
                handleRadio={this.handleRadio}
              />
            </Col>
          </Row>
        </div>
      </>
    );
  }
}
