import React, { Component } from "react";
import { Col, Row } from "react-bootstrap";
import Formulir from "./Formulir";

import NavbarComponent from "./NavbarComponent";
import Tabel from "./Tabel";
import Voting from "./Voting";

import Electionabi from "../contracts/Election.json";
import Web3 from "web3";

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      kandidate: [],
      nama: "",
      vote: 0,
      id: "",
      radio: 0,
      currentaccount: "0x0",
      loader: true,
      electionsm: "",
    };
    this.loadWeb3();
    this.LoadBlockchainData();
  }
  loadWeb3 = async () => {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum);
      await window.ethereum.enable();
    } else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider);
    } else {
      window.alert(
        "Non-ethereum browser detected. you should consider trying metamask!"
      );
    }
  };

  LoadBlockchainData = async () => {
    this.state.loader(true);
    const web3 = window.web3;

    const accounts = await web3.eth.getAccounts();
    const account = accounts[0];
    console.log(account);
    this.state.currentaccount(account);
    const networkId = await web3.eth.net.getId();

    const networkData = Electionabi.networks[networkId];

    if (networkData) {
      const election = new web3.eth.Contract(
        Electionabi.abi,
        networkData.address
      );
      const candidate1 = await election.methods.candidates(1).call();
      // const candidate1id = candidate1.id;
      // const candidate1name = candidate1.name;
      // const candidate1votecount = candidate1.votecount;

      const candidate2 = await election.methods.candidates(2).call();
      // const candidate2id = candidate2.id;
      // const candidate2name = candidate2.name;
      // const candidate2votecount = candidate2.votecount;

      setCandidate1(candidate1);
      setCandidate2(candidate2);

      setElectionsm(election);
      setLoader(false);
    } else {
      window.alert("the smart contract is not deployed current network");
    }
  };

  if(Loader) {
    return (
      <div class="spinner-border text-primary" role="status">
        <span class="visually-hidden"></span>
      </div>
    );
  }

  handleChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
    });
  };

  handleSubmit = (event) => {
    event.preventDefault();
    if (this.state.kandidate.length >= 3) {
      alert("cuma 3 calon");
    } else {
      this.setState({
        kandidate: [
          ...this.state.kandidate,
          {
            id: this.state.kandidate.length + 1,
            nama: this.state.nama,
            vote: 0,
            radio: 0,
          },
        ],
      });
    }
    this.setState({
      id: "",
      nama: "",
      vote: 0,
      radio: 0,
    });
  };

  handleVote = (event) => {
    event.preventDefault();

    console.log(this.state.id);

    if (this.state.radio === "") {
      alert("pilih dulu");
    } else {
      // alert("nomor " + this.state.id);
      const selaindipilih = this.state.kandidate
        .filter((noncalon) => noncalon.id !== this.state.id)
        .map((filternoncalon) => {
          return filternoncalon;
        });
      this.setState({
        kandidate: [
          ...selaindipilih,
          {
            id: this.state.id,
            nama: this.state.nama,
            vote: this.state.vote + 1,

            radio: this.state.radio,
          },
        ],
      });
    }
  };

  handleRadio = (event) => {
    event.preventDefault();

    const pilih = this.state.kandidate
      .filter((calon) => calon.id === Number(event.target.value))
      .map((filtercalon) => {
        return filtercalon;
      });
    console.log(pilih[0]);
    this.setState({
      id: pilih[0].id,
      nama: pilih[0].nama,
      vote: pilih[0].vote,
      radio: pilih[0].radio,
    });
  };

  render() {
    return (
      <>
        <NavbarComponent account={this.state.currentaccount} />
        <div className="container mt-5">
          <Row>
            <Col lg="4">
              <Formulir
                {...this.state}
                handleChange={this.handleChange}
                handleSubmit={this.handleSubmit}
              />
              <br></br>
              <Tabel kandidate={this.state.kandidate} />
            </Col>
            <Col>
              <Voting
                kandidate={this.state.kandidate}
                handleVote={this.handleVote}
                handleRadio={this.handleRadio}
              />
            </Col>
          </Row>
        </div>
      </>
    );
  }
}
