import React from "react";
import { Card, CardDeck, Form } from "react-bootstrap";
import foto from "../img/zakie.png";

const Voting = ({ kandidate, handleVote, handleRadio }) => {
  return (
    <div>
      <Form onSubmit={handleVote}>
        <CardDeck>
          {kandidate.map((calon, index) => {
            return index < 3 ? (
              <Card key={index}>
                <Card.Body>
                  <Card.Title>{calon.id}</Card.Title>
                  <Card.Img variant="top" src={foto} />
                  <Card.Text className="text-center">
                    kandidat nomor {index + 1}
                  </Card.Text>
                </Card.Body>
                <Card.Footer>
                  <div className="ml-2">
                    <div className="form-check"></div>
                    <input
                      className="form-check-input"
                      type="radio"
                      name="flexRadioDefault"
                      id="flexRadioDefault1"
                      value={calon.id}
                      onChange={(event) => handleRadio(event)}
                    ></input>
                    <label
                      className="form-check-label"
                      htmlFor="flexRadioDefault1"
                    >
                      {calon.nama}
                    </label>
                  </div>
                </Card.Footer>
              </Card>
            ) : (
              <div></div>
            );
          })}
        </CardDeck>

        {kandidate.length > 0 ? (
          <div className="d-grid">
            <button type="submit" className="btn btn-primary btn-lg mt-3">
              Pilih
            </button>
          </div>
        ) : (
          <div></div>
        )}
      </Form>
    </div>
  );
};

export default Voting;
